import React, { Component } from 'react';

import { Modal } from 'react-bootstrap';

import './style.css';

class ModalPopup extends Component {
    constructor(props) {
        super(props);

        this.handleClose = this.handleClose.bind(this);
    }

    handleClose() {
        this.props.handleClose();
    }

    render() {
        const { openedModal } = this.props;

        return (
            <Modal show={openedModal !== ''} 
                   animation={false} 
                   backdropClassName='backdrop-opacity'>
             <h4 className='modal-title'>Create account</h4>
        <Modal.Body>
        <form action="/action_page.php">
    <input type="text" id="fname" name="firstname" placeholder="Your name.."/>

    <input type="text" id="lname" name="lastname" placeholder="Your last name.."/>

    <input type="text" id="lname" name="lastname" placeholder="Date of birth"/>

    <input type="text" id="lname" name="lastname" placeholder="Email address"/>

    <input type="text" id="lname" name="lastname" placeholder="Password"/>

    <input type="submit" value="Submit"/>
  </form>
        </Modal.Body>

          </Modal>
        )
    }
}

export default ModalPopup;
